mapper.update:
UPDATE comment
SET contents = #{ contents }
WHERE COMMENT_no = #{ commentNo }

mapper.select:
SELECT COMMENT_no, product_no, WRITER, contents, REG_DATE, GROUP_no, STEP, DEPTH 
FROM comment
WHERE comment_no = #{ commentNo }

mapper.update:
UPDATE comment
SET STEP = STEP + 1
WHERE GROUP_no = #{ groupNo } AND STEP > #{ step }
