mapper.update:
UPDATE member
SET active = 0
WHERE id = #{ id }

UPDATE member
SET active = 1
WHERE id = #{ id }

UPDATE member
SET blackList = 1, type= "ROLE_BLACKLIST"
WHERE id = #{ id }

UPDATE member
SET blackList = 0, type= "ROLE_USER"
WHERE id = #{ id }

update member
set 
	 nickname = #{nickname}
	,phone = #{phone}
	,postcode = #{postcode}
	,roadAddr = #{roadAddr}
	,jibunAddr = #{jibunAddr}
	,detailAddr = #{detailAddr}
	,extraAddr = #{extraAddr}
where id = #{id}

update member 
set img_file_name = #{imgFileName}
where id = #{id}

mapper.select:
select img_file_name
from member
where id = #{id}

mapper.update:
update member
set introduction = #{introduction}
where id = #{id}
