mapper.update:
UPDATE product
SET DELETED = true
WHERE product_no = #{ productNo }

UPDATE product
SET type = #{ type }
	,category = #{ category }
	, name = #{ name }
	, price = #{ price }
	, contents = #{ contents }
	, count = #{ count }
WHERE product_no = #{ productNo }

mapper.insert:
	INSERT INTO productfile (
		
	)
	VALUES (
		#{ productNo } 
        , #{ saveFileName }
        , #{ rawFileName }
        , #{ flag }
	)

mapper.update:
update productfile
set save_file_name = #{ saveFileName }
where product_no = #{ productNo } and flag = 1

mapper.insert:
insert into heart(
	id, product_no
)
values(
	#{id},
	#{productNo}
)

mapper.select:
select id, product_no
from heart
where id = #{id} and product_no = #{productNo}

select count(*)
from heart
where id = #{id} and product_no = #{productNo}

select count(product_no) as cnt
from heart
where product_no = #{productNo}
group by product_no;
