mapper.insert:
insert into review(
	deal_no, contents, rating
)
values(
	#{dealNo},
	#{contents},
	#{rating}
)

	INSERT INTO reviewfile (
		deal_no, save_file_name, raw_file_name
	)
	VALUES (
		  #{ dealNo } 
        , #{ saveFileName }
        , #{ rawFileName }
	)

mapper.select:
select review_file_no,  
from reviewfile 
where deal_no = #{ dealNo }

mapper.delete:
delete
from review 
where deal_no = #{ dealNo }

mapper.update:
UPDATE review
SET contents = #{ contents } , rating = #{ rating }   
WHERE deal_no = #{ dealNo }

mapper.select:
select deal_no, contents, rating, reg_date
from review
where deal_no = #{ dealNo }

mapper.delete:
delete
from reviewfile
where deal_no = #{ dealNo }
